import { Component, OnInit } from '@angular/core';
import { KeycloakService } from './Services/keycloak/keycloak.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  template: '<router-outlet></router-outlet>'
})
export class AppComponent implements OnInit {
  constructor(
    private keycloak: KeycloakService,
    private router: Router
  ) {}

  async ngOnInit() {
    try {
      const userRoles = this.keycloak.getUserRoles();

      // If in Dashboard but not admin -> go to FrontOffice
      if (window.location.port === '4200' && !userRoles.includes('admin')) {
        window.location.href = 'http://localhost:4201';
        return;
      }

      // If in FrontOffice but is admin -> go to Dashboard
      if (window.location.port === '4201' && userRoles.includes('admin')) {
        window.location.href = 'http://localhost:4200';
        return;
      }
    } catch (error) {
      console.error('Error during role check:', error);
      // Handle error appropriately, maybe redirect to a safe location
    }
  }
}
