import { Component, OnInit } from '@angular/core';
import { KeycloakService } from './Services/keycloak/keycloak.service';
import { Location } from '@angular/common';

@Component({
  selector: 'app-root',
  template: '<router-outlet></router-outlet>'
})
export class AppComponent implements OnInit {
  private readonly DASHBOARD_PORT = '4200';
  private readonly FRONT_OFFICE_PORT = '4201';
  private readonly DASHBOARD_URL = 'http://localhost:4200';
  private readonly FRONT_OFFICE_URL = 'http://localhost:4201';

  constructor(
    private keycloak: KeycloakService,
    private location: Location
  ) {}

  async ngOnInit(): Promise<void> {
    try {
      const userRoles = await this.keycloak.getUserRoles();
      const currentPort = window.location.port;

      // If in Dashboard but not admin -> go to FrontOffice
      if (currentPort === this.DASHBOARD_PORT && !userRoles.includes('admin')) {
        window.location.href = this.FRONT_OFFICE_URL;
        return;
      }

      // If in FrontOffice but is admin -> go to Dashboard
      if (currentPort === this.FRONT_OFFICE_PORT && userRoles.includes('admin')) {
        window.location.href = this.DASHBOARD_URL;
        return;
      }

    } catch (error) {
      console.error('Error during role check:', error);
      // Fallback to a safe location if needed
      this.location.replaceState('/');
    }
  }
}
