import { Component, OnInit } from '@angular/core';
import { KeycloakService } from "./services/keycloak/keycloak.service";
import {environment} from "./environments/environment";

@Component({
  selector: 'app-root',
  template: '<router-outlet></router-outlet>'
})
export class AppComponent implements OnInit {
  constructor(private keycloakService: KeycloakService) {}

  async ngOnInit() {
    try {
      const initialized = await this.keycloakService.init();
      if (!initialized) return;

      if (this.keycloakService.isAdmin()) {
        window.location.href = environment.adminUrl;
      } else if (this.keycloakService.isUser()) {
        window.location.href = environment.userUrl;
      } else {
        alert('Access Denied: No matching role');
        await this.keycloakService.logout();
      }
    } catch (error) {
      console.error('Initialization error:', error);
    }
  }
}
